Modal.elements=[];function Modal(options={}){this.opt=Object.assign({cssClass:[],destroyOnClose:true,closeMethods:["button","overlay","escape"],footer:false},options);this.template=document.querySelector(`#${this.opt.templateId}`);if(!this.template){console.error(`${this.opt.templateId} does not exist`)}const{closeMethods:closeMethods}=this.opt;this._allowButtonClose=closeMethods.includes("button");this._allowBackdropClose=closeMethods.includes("overlay");this._allowEscapeClose=closeMethods.includes("escape");this._footerBtns=[];this._handleEscapeKey=this._handleEscapeKey.bind(this)}Modal.prototype._getScrollbarWidth=()=>{if(this._scrollbarWidth)return this._scrollbarWidth;const div=document.createElement("div");Object.assign(div.style,{overflow:"scroll",position:"absolute",top:"-9999px"});document.body.appendChild(div);this._scrollbarWidth=div.offsetWidth-div.clientWidth;document.body.removeChild(div);return this._scrollbarWidth};Modal.prototype._build=function(){const content=this.template.content.cloneNode(true);this._backdrop=document.createElement("div");this._backdrop.className="modal__backdrop";const container=document.createElement("div");container.className="modal__container";this.opt.cssClass.forEach((className=>{if(typeof className==="string"){container.classList.add(className)}}));if(this._allowButtonClose){const closeBtn=this._createButton("&times;","modal__close",(()=>this.close()));container.append(closeBtn)}const modalContent=document.createElement("div");modalContent.className="modal__content";modalContent.append(content);container.append(modalContent);if(this.opt.footer){this._modalFooter=document.createElement("div");this._modalFooter.className="footer__btn";this._renderFooterContents();this._renderFooterButton();container.append(this._modalFooter)}this._backdrop.append(container);document.body.append(this._backdrop)};Modal.prototype.setFooterContent=function(html){this._footerContent=html;this._renderFooterContents()};Modal.prototype.addFooterBut